    //方式1
    String str1 = i + "";

    //方式2
    String str2 = i.toString();

    //方式3
    String str3 = String.valueOf(i);

    //String -> 包装类(Integer)
    String str4 = "12345";
    Integer i2 = Integer.parseInt(str4);//使用到自动装箱
    Integer i3 = new Integer(str4);//构造器
}

