        arrayList.add(new Dog(10, "小黄"));
        arrayList.add(new Dog(12, "小黑"));
        arrayList.add(new Dog(15, "小懒"));
        for (Dog o :arrayList) {
            System.out.println(o.getName() + "-" + o.getAge());
        }

    }
}

